import {Container, Table } from 'reactstrap';
import { Switch, Route, Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { useRouteMatch } from 'react-router-dom';

import CreateUser from './CreateUser';
import ViewUser from './ViewUser';
import EditUser from './EditUser';

function Index() {
	const [ userData, userDataChange ] = useState( null );
	const history = useHistory();
	const { path, url } = useRouteMatch();


	// **
	// Functions for 
	// each user details
	// **
	const LoadEdit = ( id ) => {
		history.push( '/users/edit/' + id );
	};

	const LoadView = ( id ) => {
		history.push( '/users/view/' + id );
	};

	const DeleteUser = ( id ) => {
		if ( window.confirm( 'Do you want to remove?' ) ) {
            fetch( "http://localhost:3004/users/" + id, {
                method: "DELETE"
            }).then(( res ) => {
                alert( 'Removed successfully.' )
                window.location.reload();
            }).catch(( err ) => {
                console.log( err.message );
            })
        }
	};


	// **
	// Getting data 
	// from db.json
	// **
	useEffect(() => {
		fetch( "http://localhost:3004/users" ).then(( data ) => {
			return data.json();
		}).then(( resp ) => {
			userDataChange( resp );
		}).catch(( err ) => {
			console.log( err.message );
		})
	}, []);



	return (
		<Container>
			<Card>
			<div className='mt-3 text-right'>
				<Link to={ `${url}/create` } className="btn btn-primary add-user_btn fw-bold fs-6">+ Add User</Link>
			</div>

			<Table className='mt-3 table table-hover'>
				<thead>
					<tr>
						<th>ID</th>
						<th>Profile</th>
						<th>Email</th>
						<th>First Name</th>
						<th>Last Name</th>
						<th />
					</tr>
				</thead>

				<tbody>
					{ /* Displaying lists of users */ }
					{ userData &&
						userData.map ( user => (
							<tr key = { user.id }>
								<td>{ user.id }</td>
								<td>{ user.profile }</td>
								<td>{ user.email }</td>
								<td>{ user.firstName }</td>
								<td>{ user.lastName }</td>
								<td>
									<a onClick={() => { LoadEdit(user.id) }} className="btn" color='secondary'>Edit</a>
									<a onClick={() => { DeleteUser(user.id) }} className="btn" color='secondary'>Delete</a>
									<a onClick={() => { LoadView(user.id) }} className="btn" color='secondary'>View</a>
								</td>
							</tr>
						))
					}
				</tbody>
			</Table>
			</Card>

			<Switch>
				<Route path={ `${path}/create` } >
					<CreateUser />
				</Route>
				<Route path={ `${path}/view/:userid` }>
					<ViewUser />
				</Route>
				<Route path={ `${path}/edit/:userid` }>
					<EditUser />
				</Route>
			</Switch>
		</Container>
	);
}

export default Index;
