const { useEffect, useState } = require("react");
const { useParams } = require("react-router-dom");

const ViewUser = () => {
    const { userID } = useParams();

    const [ userData, userDataChange ] = useState({});

    useEffect(() => {
        fetch( "http://localhost:3004/users/" + userID ).then(( data ) => {
			return data.json();
		}).then(( resp ) => {
			userDataChange( resp );
		}).catch(( err ) => {
			console.log( err.message );
		})
    }, []);

    return (
        <div>
            <h1>User Details</h1>
            {/* { userData && 
                <p>The employee name is: { userData.firstName } ({ userData.id })</p>
            } */}

            { userData &&
				userData.map ( user => (
					<tr key = { user.id }>
						<td>{ user.id }</td>
						<td>{ user.profile }</td>
						<td>{ user.email }</td>
						<td>{ user.firstName }</td>
						<td>{ user.lastName }</td>
						<td>
							<a onClick={() => { LoadEdit(user.id) }} className="btn" color='secondary'>Edit</a>
							<a onClick={() => { DeleteUser(user.id) }} className="btn" color='secondary'>Delete</a>
							<a onClick={() => { LoadView(user.id) }} className="btn" color='secondary'>View</a>
						</td>
					</tr>
				))
			} 
        </div>
    );
};

export default ViewUser;
